import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';

import './intro.css';
import myDevices from './devices.svg';
import myImage from './MeMe.png';

const theme = createTheme({
    typography: {
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
      ].join(','),
    },
  });

theme.typography.h3 = {
  fontSize: '1.2rem',
  '@media (min-width:600px)': {
    fontSize: '3rem',
  },
  [theme.breakpoints.up('md')]: {
    fontSize: '3rem',
  },
};

theme.typography.h4 = {
    fontSize: '0.8rem',
    '@media (min-width:600px)': {
      fontSize: '2.2rem',
    },
    [theme.breakpoints.up('md')]: {
      fontSize: '2.5rem',
    },
  };
export default function Intro(){
    return(
        <div className='intro'>
            <div className='intro-heading'>
            <ThemeProvider theme={theme}>
                <Typography variant="h3">Designer, Frontend & Backend Developer</Typography>
            </ThemeProvider>
            <br></br>
            <Typography variant="h6">I design and code efficiently, and I love what I do.</Typography>
            </div>
            <div className='profile'>
                <img src={myImage} alt="error"></img>
            </div>

            <img src={myDevices} className='devices' alt='Error'></img>
            <div className='detail-intro'>
            <ThemeProvider theme={theme}>
                <Typography variant="h4" color={"white"} style={{paddingTop: "50px"}}>
                    Hi, Iâ€™m Mohan. Nice to meet you.
                </Typography>
            </ThemeProvider>
            <Box
                sx={{
                display: 'flex',
                alignItems: 'center',
                width: 'fit-content',
                border: (theme) => `1px solid ${theme.palette.divider}`,
                borderRadius: 1,
                bgcolor: 'background.paper',
                color: 'text.secondary',
                '& svg': {
                    m: 1.5,
                },
                '& hr': {
                    mx: 0.5,
                },
                }}
            >
                <FormatAlignLeftIcon />
                <FormatAlignCenterIcon />
                <FormatAlignRightIcon />
                <Divider orientation="vertical" flexItem />
                <FormatBoldIcon />
                <FormatItalicIcon />
            </Box>
            </div>
        </div>
    );
}
